import okTooltipStatus from '../images/ok-tooltip-status.svg';
import errorTooltipStatus from '../images/error-tooltip-status.svg';

function InfoTooltip({ tooltipStatus, isOpen, onClose }) {
  return (
    <div className={`popup ${isOpen ? 'popup_opened' : ''}`}>
          <div className="popup__container">
              <img className="popup__title"src={(tooltipStatus === 'ok') ? okTooltipStatus : errorTooltipStatus} />
              <h2 className="popup__title">{(tooltipStatus === 'ok') ? "Вы успешно зарегистрировались!" : `Что-то пошло не так! Попробуйте ещё раз.`}</h2>
              <button className="popup__close-button" type="button" aria-label="Закрыть" onClick={onClose}></button>
          </div>
    </div>
  );
}

export default InfoTooltip;
