function SignUp() {
  return (
    <div className="popup popup_opened">
          <div className="popup__container">
            <form className="popup__form popup__add" name="add-form"> 
              <h2 className="popup__title">Регистрация</h2>
                <input className="popup__input" type="text" name="name" placeholder="Ema" autoComplete="off" minLength="2" maxLength="40" required/>
                <span className="name-error popup__input-error"></span>
                <input className="popup__input" type="text" name="about" placeholder="Занятие" autoComplete="off" minLength="2" maxLength="200" required/>
                <span className="about-error popup__input-error"></span>
              <button className="popup__submit-button" type="submit">Зарегистрирвоаться</button>
            </form>
            <button className="popup__close-button" type="button" aria-label="Закрыть"></button>
          </div>
    </div>
  );
}

export default SignUp;
