import '../../src/index.css';
import React from 'react';
import { useEffect } from 'react';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup({ isOpen, onClose, onAddCard }) {
  const nameRef = React.useRef();
  const linkRef = React.useRef();

  useEffect(() => {
    if (!isOpen) {
      nameRef.current.value = null;
      linkRef.current.value = null;
    }
  }, [isOpen]);

  function handleSubmit(e) {
    e.preventDefault();

    onAddCard(nameRef.current.value, linkRef.current.value);
  } 

  return (
    <PopupWithForm isOpen={isOpen} onClose={onClose} popupName={'add'} title={'Новое место'} buttonText={'Создать'}>
      {/* <div className={`popup popup_type_add ${isOpen && 'popup_opened'}`}>
