function Register() {
  return (
    <div className="auth">
          <div className="auth__container">
            <form className="auth__form"> 
              <h2 className="auth__title">Регистрация</h2>
                <input className="auth__input" type="email" name="email" placeholder="Email" autoComplete="off" minLength="2" maxLength="40" required/>
                <span className="email-error popup__input-error"></span>
                <input className="auth__input" type="password" name="password" placeholder="Пароль" autoComplete="off" minLength="2" maxLength="200" required/>
                <span className="password-error __input-error"></span>
              <button className="auth__submit-button" type="submit">Зарегистрироваться</button>
            </form>
          </div>
    </div>
  );
}

export default Register;
