function Register() {
  return (
    <div className="auth">
          <div className="auth__container">
            <form className="au__form popup__add" name="add-form"> 
              <h2 className="popup__title">Регистрация</h2>
                <input className="popup__input" type="text" name="name" placeholder="Email" autoComplete="off" minLength="2" maxLength="40" required/>
                <span className="name-error popup__input-error"></span>
                <input className="popup__input" type="text" name="about" placeholder="Пароль" autoComplete="off" minLength="2" maxLength="200" required/>
                <span className="about-error popup__input-error"></span>
              <button className="popup__submit-button" type="submit">Зарегистрироваться</button>
            </form>
          </div>
    </div>
  );
}

export default Register;
